def generate_gamma_table(steps=255, size=255, gamma=2.5):
    """
    Returns a dictionary of gamma corrected values
    """
    lookup = {}
    for i in range(steps + 1):
        gval = int(size * (((size / steps * (i + 1)) / size) ** gamma))
        lookup[i] = gval
    return lookup
